sp_widget.client_script:
api.controller=function() {
  /* widget controller */
  var c = this;
	c.test=function(){
		c.data.temp1=c.sellerName;
		c.data.temp2=c.carModel;
		c.data.temp3=c.carPrice;
		c.data.temp4=c.carAge;
		c.server.update();
	}
};

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	var gr=new GlideRecord('x_1299822_car_sell_carsell');
	gr.initialize();
