sp_widget.css:
/* Reset some default styles and provide a basic styling */
body {
  font-family: Arial, sans-serif;
  margin: 20px;
}

/* Style for the widget container */
div {
  max-width: 800px;
  margin: 0 auto;
  padding: 20px;
  background-color: #f5f5f5;
  border: 1px solid #ddd;
  border-radius: 8px;
}

/* Header styling */
h1 {
  text-align: center;
  color: #333;
}

/* Table styling */
table {
  width: 100%;
  border-collapse: collapse;
  margin-top: 20px;
}

/* Table header styling */
thead {
  background-color: #4CAF50;
  color: white;
}

th, td {
  padding: 12px;
  text-align: left;
}

/* Alternating row background colors for better readability */
tbody tr:nth-child(even) {
  background-color: #f2f2f2;
}

/* Hover effect on table rows */
tbody tr:hover {
  background-color: #e0e0e0;
}

/* Responsive styling for smaller screens */
@media screen and (max-width: 600px) {
  table {
    overflow-x: auto;
  }
}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	data.table=[];
	var gr=new GlideRecord('x_1299822_car_sell_carsell');
	var count=0;
	gr.orderBy('number');
	gr.query();

	while(gr.next()){
		count++;
		var obj={};
		obj.id=count;
		obj.number=gr.getValue('number');
		obj.sellername=gr.getDisplayValue('u_reference_1');
		obj.carmodel=gr.getValue('car_model');
		obj.price=gr.getValue('price');
		obj.age=gr.getValue('car_age');
		data.table.push(obj)
	}

})();
